app.factory('myFactory', function($filter, $http) {
    var num1 = '10';

    var factoryFunction = function() {
        this.getProductFactory = function(scope) {
            $http.get("/product_procedure").success(function(data) {
                    console.log("Success call API");
                    console.log(data);
                    return scope.modelData.productData = data.product[0];
                })
                .error(function(data) {
                    console.log("Error call API");
                });
        };

        this.getReviewFactory = function(scope) {
            $http.get("/review").success(function(data) {
                    console.log("Success call API");
                    console.log(data);
                    return scope.modelData.reviewData = data.users;
                })
                .error(function(data) {
                    console.log("Error call API");
                });
        };
    };

    return new factoryFunction;

});
